    // Start is called before the first frame update
    void Start()
    {
        //Debug.Log(this.gameObject);
        FetchCountryList();
    }

    public void FetchCountryList()
    {
        warningPopup.SetActive(false);
        GetCode();
    }

    public void GetCode()
    {

        var statsList = MiniJSON.Json.Deserialize(countryListText.text) as IDictionary;

        var userDetails = (IDictionary)statsList["result"];
        //Debug.Log(userDetails.Keys.Count);
        List<string> temp = new List<string>();
        temp = userDetails.Keys.Cast<string>().ToList();
        // Debug.Log(temp.Count);
        countryCode.options.RemoveAt(0);
        countryCode.AddOptions(temp);
        code = userDetails;
        if (!PlayerPrefs.HasKey("phone") || isSignUp)
        {
            countryCode.value = PlayerPrefs.GetInt("SelectedCountryCodeCount");
            string selectedCountry = code[countryCode.options[countryCode.value].text].ToString();
            LoginManager.Instance.loginCountryCode.text = "+" + selectedCountry;
            LoginManager.Instance.registerCountryCode.text = "+" + selectedCountry;
        }
        else
        {
            countryCode.value = PlayerPrefs.GetInt("SelectedCountryCodeCount");
            LoginManager.Instance.loginCountryCode.text = PlayerPrefs.GetString("phone");
        }

    }
    public void selectedValue()
    {
        string selectedCountry = code[countryCode.options[countryCode.value].text].ToString();
        PlayerPrefs.SetInt("CountryCodeCount", countryCode.value);
        PlayerPrefs.SetString("CountryCode", selectedCountry);
        Debug.Log(PlayerPrefs.GetInt("CountryCodeCount"));
        Debug.Log(PlayerPrefs.GetString("CountryCode"));
        LoginManager.Instance.loginCountryCode.text = "+" + selectedCountry;
        LoginManager.Instance.registerCountryCode.text = "+" + selectedCountry;
    }    
